/* track midi state so one object 
 gives you access to everthing going on the keyboard at the same time.
 in particular:
- currentNotesOn (with values)
- controllerValues

and send events whenever things changed, including
- full state
- the thing that changed
- what it was before 

and include ability to reset the state
*/

var portname = msg.payload.portname;
var event = msg.payload.event;
var params = msg.payload.params;

var midistate = global.get("midistate");

if(!midistate){
    midistate = {};
}

let thisdevice = {portname: portname,
                notesOn : {},
                controlVals : {}};

if(midistate[portname]){
    thisdevice = midistate[portname];
}

if(event == "noteon"){
    thisdevice.notesOn[params.note] = params.velocity;
}

if(event == "noteoff"){
    delete thisdevice.notesOn[params.note];
}
if(event == "cc"){
    thisdevice.controlVals[params.controller] = params.value;
}


midistate[portname] = thisdevice;

global.set("midistate", midistate);

msg.payload.midistate = midistate;
return msg;
